<?php
namespace App\Http\ViewComposer;


use App\Models\Post;
use App\Models\Social;
use App\Models\Website;
use App\Models\Category;
use Illuminate\View\View;

class SensitiveComposer
{
    public function compose(View $view){
        $recent_post = Post::orderBy('created_at', 'desc')->take(5)->get();
        $sports = Post::where('category_id', 6)->where('status', 'publish')->orderBy('created_at', 'desc')->take(5)->get();
        $technology = Post::where('category_id', 12)->where('status', 'publish')->orderBy('created_at', 'desc')->take(5)->get();
        $world = Post::where('category_id', 1)->where('status', 'publish')->orderBy('created_at', 'desc')->take(5)->get();
        $politics = Post::where('category_id', 8)->where('status', 'publish')->orderBy('created_at', 'desc')->take(5)->get();
        $lifestyle = Post::where('category_id', 11)->where('status', 'publish')->orderBy('created_at', 'desc')->take(5)->get();
        $health = Post::where('category_id', 5)->where('status', 'publish')->orderBy('created_at', 'desc')->take(5)->get();
        $social_data = Social::first();
        $website_data = Website::first();
        $social_info = Social::first();
        $website_info = Website::first();
        $category = Category::orderBy('priority', 'asc')->get();
        $featured_news = Post::where('is_featured', 1)->where('status', 'publish')->orderBy('created_at', 'desc')->take(5)->get();
        $view->with('recent_posts', $recent_post)
        ->with('social_media', $social_data)
        ->with('website_data', $website_data)
        ->with('category_list', $category)
        ->with('sports', $sports)
        ->with('technology', $technology)
        ->with('world', $world)
        ->with('politics', $politics)
        ->with('lifestyle', $lifestyle)
        ->with('health', $health)
        ->with('featured_news', $featured_news)
        ->with('social_info', $social_info)
        ->with('website_info',$website_info);
    }
}